List qualifikationen, List arbeitsumfeld) {

this.aufgaben = aufgaben;

this.erwartungen = erwartungen;

this.qualifikationen = qualifikationen;

this.arbeitsumfeld = arbeitsumfeld;
}
private Testautomatisierer(Builder builder) {

setAufgaben(builder.aufgaben);

setErwartungen(builder.erwartungen);

setQualifikationen(builder.qualifikationen);

setArbeitsumfeld(builder.arbeitsumfeld);
}
