// -Durchführung und Überwachung automatisierter Testsuites

// -Sie unterstützen Lösungen für Continuous Integration Prozesse

// -Sie verantworten die automatische Testdatengenerierung

// -Analyse der Ergebnisse und Erstellung von Reports

// -Leitung, Steuerung und Koordination von Testautomatisierungs-Projekten
}
public List getErwartungen() {

return erwartungen;
}
public void setErwartungen(List erwartungen) {

this.erwartungen = erwartungen;

// erwartungen -->

// -Spannende, abwechslungsreiche Projekte bei namenhaften Kunden vor Ort

// -Freiräume und Gestaltungsmöglichkeiten für eigene Ideen

// -strukturierter Onboardingprozess mit Job Coach

// -Zusammenarbeit mit dem besten Team der Welt *
}
public List getQualifikationen() {

return qualifikationen;
}
