this.qualifikationen = qualifikationen;

// qualifikationen -->

// -Technische Ausbildung, Hochschulstudium oder einschlägige Berufserfahrung im Bereich Softwareentwicklung bzw. Testautomatisierung

// -Teamgeist und Leidenschaft für Softwaretest

// -Erfahrung mit gängigen Programmiersprachen, Entwicklungsumgebungen und -methoden

// -Starke Kundenorientierung sowie Offenheit gegenüber Menschen und Ideen

